import React, { useState } from 'react'
import './catalogPage.css'
import ReactPaginate from 'react-paginate'

export default function CatalogPage(props) {

    const [currentPage, setCurrentPage] = useState(1)

    const [productsPerPage, setProductsPerPage] = useState(6)

    const changePage = (event) => {
        setCurrentPage(Number(event.target.id))
    }

    const toLeft = () => {
        setCurrentPage()
    }

    const pages = []

    for(let i = 1; i <= Math.ceil(props.products.length / productsPerPage); ++i) {
        pages.push(i)
    }

    const indexOflastProduct = currentPage * productsPerPage 

    const indexOfFirstProduct = indexOflastProduct - productsPerPage

    const currentProducts = props.products.slice(indexOfFirstProduct, indexOflastProduct)

    const renderProducts = product => {
        return(
            <ul className="itemsCatalog">
                {
                    product.map((product, index) => {
                        return (
                            <li 
                            className="isolatedItems"
                            key={index}
                            >
                                <h2>{product.name}</h2>
                                <img src={product.image} alt={product.name}/>
                            </li>
                        )
                    } )
                }  
            </ul>
        )
    }

    const renderPageNumbers = pages.map(number => {
        return (
                <li
                className={currentPage == number ? "activePage": "disabePage"}
                key={number} 
                id={number}
                onClick={changePage}
                >
                .
                </li>
        )
    })

    return (
            <div className="itemsContainer">
                {renderProducts(currentProducts)}
            <ul className="paginateContainer">
            <svg class="previousBtn" onClick={toLeft}  viewBox="0 0 24 24"  fill="none"  stroke="currentColor"  stroke-width="2"  stroke-linecap="round"  stroke-linejoin="round">  
                <circle cx="12" cy="12" r="10" />  
                <polyline points="12 8 8 12 12 16" />  
                <line x1="16" y1="12" x2="10.5" y2="12" />
            </svg>
                {renderPageNumbers}
                <svg class="nextBtn"  viewBox="0 0 24 24"  fill="none"  stroke="currentColor"  stroke-width="2"  stroke-linecap="round"  stroke-linejoin="round">  
                    <circle cx="12" cy="12" r="10" />  
                    <polyline points="12 16 16 12 12 8" />  
                    <line x1="8" y1="12" x2="13.5" y2="12" />
                </svg>
            </ul>
            </div>
    )
}
