import { useState } from 'react'
import './shoppingCart.css'

export default function ShoppingCart() {

    const [btn, setBtn] = useState("flex justify-around align-center mr-4 w-12 py-1 flex-row rounded-full duration-700 cursor-pointer")

    const [initialValue, setValue] = useState("0")

    const [colorP, setColorP] = useState("text-gray-500 text-xl")

    const [colorCart, setColorCart] = useState("h-6 w-6 title")

    const [showWindow, setShowWindow] = useState("checkoutContainer");

    let changeColors = () => {
        if(btn === "flex justify-around align-center mr-4 w-12 py-1 flex-row rounded-full duration-700 cursor-pointer"){
            setBtn("flex justify-around mr-4 w-20 py-1 flex-row rounded-full changebck duration-700 cursor-pointer")
            setColorP("text-white text-xl")
            setColorCart("h-6 w-6 text-white")
            setShowWindow("checkoutContainer1 duration-700")
        } else {
            setBtn("flex justify-around align-center mr-4 w-12 py-1 flex-row rounded-full duration-700 cursor-pointer")
            setColorP("text-gray-500 text-xl")
            setColorCart("h-6 w-6 title")
            setShowWindow("checkoutContainer duration-700")
        }
    }

    return (
        <>
            <div>
                <div className={btn} onClick={changeColors} >
                    <svg className={colorCart}   fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z"/>
                    </svg>
                    <p className={colorP}> {initialValue} </p>
                </div>
            </div>
            <div className={showWindow}>
                <h2>O teu saco permanece vazio!</h2>
                <p>Adiciona artigos para iniciares a compra</p>
            </div>
        </>
    )
}
