import { useEffect, useState } from 'react'
import './menuButton.css'

export default function MenuButton(props)  {

   const [position, setPosition] = useState("rotate-0")


   const [colorP, setColorP] = useState(props.color) 
   // VERIFICAR PORQUE O MENU PASSA PRIMEIRO PARA CINZENTO E DEPOIS RODA!!
   // porquê estar da dar só no segundo clique
   const [visibility, setVisibility] = useState("navBarLayout")

    useEffect(() => {
        const onScroll = () =>{
            if(window.scrollY < 200){
            setColorP(props.color) 
        } else {
            setColorP(props.secondaryColor)
        }
    }    
        // clean up code
        window.removeEventListener('scroll', onScroll);
        window.addEventListener('scroll', onScroll, true);
        return () => window.removeEventListener('scroll', onScroll); 
    }, [])

    let rotateMenu = () => {
        if(position == "rotate-0 duration-300"){
            setPosition("rotate-90 duration-300 mx-10")
            setColorP("h-8 w-16 title")
            setVisibility("navBarLayout1 duration-300")
        } else {
            setPosition("rotate-0 duration-300")
            setColorP(props.secondarycolor)
            setVisibility("navBarLayout duration-300")
        }
    }   

    return (
        <>
            <div 
                className={position} 
                onClick={rotateMenu}
            >
                <svg className={colorP} width="24" height="24" viewBox="0 0 24 24" stroke-width="2" 
                stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round">  
                    <path stroke="none" class="transition duration-150 ease-out hover:ease-in" d="M0 0h24v24H0z"/>  
                    <line x1="4" y1="6" x2="20" y2="6" />  
                    <line x1="4" y1="12" x2="20" y2="12" />  
                    <line x1="4" y1="18" x2="20" y2="18" />
                </svg>    
            </div>
            <nav className={visibility}>
                <ul>
                    <li className="items">
                        <a> Home </a>
                    </li> 
                    <li className="items1">
                        <a> Wish Catalog </a>
                    </li> 
                </ul>
            </nav>
        </>
    )
}
