import React, { useEffect, useState } from 'react'
import './main.css'
import CatalogPage from '../ReusableComponents/CatalogComp/CatalogPage'
import Modal from '../ReusableComponents/modalComp/Modal'

export default function Main() {

    const [products, setProducts] = useState([])

     const [pageNumber] = useState(0)

     const productsPerPage = 6

     const [indexOfProduct, setIndexOfProduct] = useState(0) 

     const productsInGroup = products.filter( (product) => product.id == indexOfProduct + 1)

     const nextBookable = () => {
        setIndexOfProduct((indexOfProduct) % productsInGroup.length)
    }

    const product = productsInGroup[indexOfProduct]


    const indexOfLastItem = productsPerPage

    const currentItems = products.slice(indexOfProduct, indexOfLastItem)

    const [modalFirstState, setModaFirstlState] = useState("modalContainer1") 
    //estado não visivel

    const [modalSecondState, setmodalSecondState] = useState("modalContainer")
    //estado visivel

     let openModal = (index) => {
        //inverte o valores dos estados
        setIndexOfProduct(index)
        if(modalFirstState === "modalContainer1"){ 
           setModaFirstlState(modalSecondState)
           setmodalSecondState("modalContainer1")
        }
    }

    let closeModal = () => {
        if(modalFirstState === "modalContainer") {
            setModaFirstlState(modalSecondState)
            setmodalSecondState("modalContainer")
        }
    } 



    useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/wishCatalog")
        .then((resp) => resp.json())
        .then((data) => setProducts(data))        
    }, []) 


    return (
        <>
            <div className="catalogContainer relative" id="main">
                <CatalogPage
                /* modalChange={openModal} */
                products={product}
                pageNumber={pageNumber}
                productsPerPage={productsPerPage}
                openModal={openModal}
                nextBookable={nextBookable}
                />
                <Modal 
                    id={currentItems.map((foodInfo) => foodInfo.id)}
                    name={currentItems.map((foodInfo) => foodInfo.name)}
                    image={currentItems.map((foodInfo) => foodInfo.image)}
                    meal={currentItems.map((foodInfo) => foodInfo.meal)}
                    description={currentItems.map((foodInfo) => foodInfo.description)}
                    extras={currentItems.map((foodInfo) => foodInfo.extras)}
                    price={currentItems.map((foodInfo) => foodInfo.price)}
                    stock={currentItems.map((foodInfo) => foodInfo.stock)} 
                    hide={modalFirstState} 
                    show={modalSecondState}
                    closeModal={closeModal}
                    products={products}
                    currentItems={currentItems}
                />
            </div>
        </>
    )
}
