import { useState } from 'react'
import './inputFiltrar.css'

export default function InputFiltrarProdutos() {


    const [width, setWidth] = useState("w-20 border-2 rounded-full py-1 px-1.5")

    const [rotate, setRotate] = useState("h-6 w-6 text-gray-500 absolute top-1.5 left-3 rotate-0")

    const [input, setInput] = useState("")



    let increaseWidth = () => {
         if(width === "w-20 border-2 rounded-full py-1 px-1.5 duration"){ 
            setWidth("stretch border-2 rounded-full py-1 pl-11 duration")
            setRotate("h-6 w-6 text-gray-500 absolute top-1.5 left-3 rotate-180  duration")
            setInput("Type your wishes...")
        }  else { 
            setWidth("w-20 border-2 rounded-full py-1 px-1.5 duration")
            setRotate("h-6 w-6 text-gray-500 absolute top-1.5 left-3 rotate-0 duration")
            setInput("")
        } 
    }

    return (
        <div className="mx-4 relative"> 
            <div className="cursor-pointer">
                <svg className="h-6 w-6 title absolute top-1.5 right-3"  width="24" height="19" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round">  <path stroke="none" d="M0 0h24v24H0z"/>  <circle cx="10" cy="10" r="7" />  <line x1="21" y1="21" x2="15" y2="15" /></svg>
            </div>
            <div className="cursor-pointer">
                <svg className={rotate} onClick={increaseWidth} width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round">  <path stroke="none" d="M0 0h24v24H0z"/>  <polyline points="15 6 9 12 15 18" /></svg>
            </div>
            <form>
                <label>
                    <input className={width}  type="text" placeholder={input} />
                </label>
            </form>
        </div>
    )
}
