import { useState } from 'react'
import { menuItems } from '../../internalApi/menuApi'
import './menuButton.css'

export default function MenuButton()  {

   const [position, setPosition] = useState("rotate-0")

   const [color, setColor] = useState("h-8 w-16 text-gray-600")

   const [visibility, setVisibility] = useState("navBarLayout")

    let rotateMenu = () => {
        if(position == "rotate-0 duration-1000"){
            setPosition("rotate-90 duration-1000 mx-10")
            setColor("h-8 w-16 title duration-1000")
            setVisibility("navBarLayout1 duration-700")
        } else {
            setPosition("rotate-0 duration-1000")
            setColor("h-8 w-16 text-gray-600 duration-1000")
            setVisibility("navBarLayout duration-700")
        }
    }   
    return (
        <>
            <div 
                className={position} 
                onClick={rotateMenu}
            >
                <svg className={color} width="24" height="24" viewBox="0 0 24 24" stroke-width="2" 
                stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round">  
                    <path stroke="none" class="transition duration-150 ease-out hover:ease-in" d="M0 0h24v24H0z"/>  
                    <line x1="4" y1="6" x2="20" y2="6" />  
                    <line x1="4" y1="12" x2="20" y2="12" />  
                    <line x1="4" y1="18" x2="20" y2="18" />
                </svg>    
            </div>
            <div className={visibility}>
                <ul>
                    {
                    menuItems.map((a) => (
                        <li
                        className="items"
                        >
                            <a>
                                {a.item}
                            </a>
                        </li> 
                        ))
                    }
                </ul>
            </div>
        </>
    )
}
