import {useState} from "react";
import MenuButton from '../ReusableComponents/MenuButton';

export default function Header()  {

    const [position, setPosition] = useState({transform: [{rotateY: '0'}]})

    let rotateButton = () => {
        if(position == {transform: [{rotateY: '0'}]}){
            setPosition({
                transform: [{rotateY: '180'}],
                timeout: '200ms',
            })
        } else {
            setPosition({
                transform: [{rotateY: '0'}],
                transition: '200ms',
            })
        }
    }

    return (
        <div class="m-0 w-screen bg-cyan-400">
            <a class="cursor-pointer" onClick={rotateButton}>
             <MenuButton />
            </a>
        </div>
    )
}
