import { useState } from 'react'
import './shoppingCart.css'
import Modal from "@material-tailwind/react/Modal";
import ModalHeader from "@material-tailwind/react/ModalHeader";
import ModalBody from "@material-tailwind/react/ModalBody";
import ModalFooter from "@material-tailwind/react/ModalFooter";

export default function ShoppingCart() {

    const [btn, setBtn] = useState("flex justify-around align-center mr-4 w-12 py-1 flex-row rounded-full duration-700")

    const [initialValue, setValue] = useState("0")

    const [colorP, setColorP] = useState("text-gray-500 text-xl")

    const [colorCart, setColorCart] = useState("h-6 w-6 title cursor-pointer")

    const [showModal, setShowModal] = React.useState(false)

    let changeColors = () => {
            setBtn("flex justify-around mr-4 w-20 py-1 flex-row rounded-full changebck duration-700")
            setColorP("text-white text-xl")
            setColorCart("h-6 w-6 text-white cursor-pointer")
    }

    let backToOrigin = () => {
        setBtn("flex justify-around mr-4 w-12 py-1 flex-row rounded-full duration-700")
        setColorP("text-gray-500 text-xl")
        setColorCart("h-6 w-6 title cursor-pointer")
    }

    return (
        <div>
            <div className={btn} onMouseEnter={changeColors} onMouseLeave={backToOrigin} onClick={(e) => setShowModalCode(true)}>
                <svg className={colorCart}   fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z"/>
                </svg>
                <p className={colorP}> {initialValue} </p>
            </div>
        </div>
    )
}
