import './modal.css'

export default function Modal(props) {


   let [stock, setStock] = useState(props.stock) 

    const changeStock = () => {
      if(stock > -1){
        setStock(stock -= 1)
      } else if(stock == -1) {
        setStock(props.stock)
      }
    }


  return (
    <div className={props.hide} >

    </div>
    )
}
