import './App.css';
import "@material-tailwind/react/tailwind.css";
import HomePage from './homepage/HomePage'
import CheckOutPage from './checkoutPage/CheckOutPage'
import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch} from "react-router-dom"

export default function App() {

  const [products, setProducts] = useState([]) // products contém todos os produtos recebidos pela API

  const [initialValue, setValue] = useState(0)

  const [isChecked, setIsChecked] = useState(false)

  let [selectedExtra]= useState([])

  const checkBoxOnChange = (event) => {
    setIsChecked(event.target.checked)
  }

  const getExtras = (extra) => {
    if(!selectedExtra.includes(extra.target.name) && (extra.target.checked)){
      selectedExtra.push(extra.target.name)
    }
  }

  console.log(selectedExtra)

  useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/wishCatalog")
        .then((resp) => resp.json())
        .then((data) => setProducts(data))        
    }, []) 

  return (
    <Router>
      <div className="App">
        <Navbar />
          <Switch></Switch>
          <HomePage
          initialValue={initialValue}
          products={products}
          checked={isChecked}
          checkBoxOnChange={checkBoxOnChange}
          getExtras={getExtras}
          />
      </div>
    </Router>
  );
}
