import React, { useEffect, useState } from 'react'
import './main.css'
import CatalogPage from '../ReusableComponents/CatalogComp/CatalogPage'
import Modal from '../ReusableComponents/modalComp/Modal'

export default function Main(props) {

    const [selectedProduct, setSelectedProduct] = useState([])

    const [modalFirstState, setModaFirstlState] = useState("modalContainer1") 

    const modalSecondState = "modalContainer"

   const productsInGroup = (evt, index) => {
       setSelectedProduct(evt.target.props.products[index])
    }

   console.log(productsInGroup)

    let openModal = (evt) => {
       setModaFirstlState(modalSecondState)
    }

    let closeModal = () => {
        setModaFirstlState("modalContainer1")
    }

     return (
         <div className="catalogContainer relative" id="main">
            <CatalogPage
                products={props.products}
                openCard={openModal}
            />
             {
                productsInGroup.map(product => 
                    <Modal 
                        id={product.id}
                        name={product.name}
                        image={product.image}
                        meal={product.meal}
                        description={product.description}
                        extras={product.extras}
                        price={product.price}
                        stock={product.stock}
                        hide={modalFirstState}
                        closeModal={closeModal}
                    />
                ) 
            }
         </div>
     )
}
