import { useEffect, useState } from 'react';
import './modal.css'

export default function Modal(props) {



    /* onClick={props.closeModal} */ // evento para fechar modal
  return (
    <div className={props.hide} >
        <div className="infoContainer">
          <svg class="h-10 w-10 text-white absolute z-10 top-2 right-3 cursor-pointer" onClick={props.closeModal} fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"/>
          </svg>
          <h1>{props.name}</h1>
          <img className="imageModal" src={props.image} />
          <p className="">{props.description}</p>
          <div className="extrasContainer">
              <h2>Extras</h2>
              <ul>
                {
                  props.extras.map( extras => {
                    <li>{extras}</li>
                  })
                }
              </ul>
          </div>
        </div>
    </div>
    )
}
