import React, { useEffect, useState, useContext } from 'react'
import './main.css'
import CatalogComp from '../ReusableComponents/CatalogComp/CatalogPage'
import Modal from '../ReusableComponents/modalComp/Modal'
import { ProductsContext } from '../ReusableComponents/CatalogComp/CatalogPage'

export default function Main() {

    const [modalFirstState, setModaFirstlState] = useState("modalContainer1") 
    //estado não visivel

    const [modalSecondState, setmodalSecondState] = useState("modalContainer")
    //estado visivel

    let openModal = () => {
        //inverte o valores dos estados
        if(modalFirstState === "modalContainer1"){ 
            setModaFirstlState(modalSecondState)
            setmodalSecondState("modalContainer1")
        }
    }

    let closeModal = () => {
        if(modalFirstState === "modalContainer") {
            setModaFirstlState(modalSecondState)
            setmodalSecondState("modalContainer")
        }
    }

    const productsFromChildren = useContext(ProductsContext)


    return (
        <>
            <div className="catalogContainer relative" id="main">
                <CatalogComp 
                modalChange={openModal}
                />
                <Modal 
                hide={modalFirstState} 
                show={modalSecondState}
                closeModal={closeModal}
                />
            </div>
        </>
    )
}
