import { useState } from 'react'
import './shoppingCart.css'

export default function ShoppingCart() {

    const [btn, setBtn] = useState("flex justify-around mr-4 border-2 w-20 py-1 flex-row rounded-full")

    const [initialValue, setValue] = useState("0")

    const [colorP, setColorP] = useState("text-gray-500")

    const [colorCart, setColorCart] = useState("h-6 w-6 title cursor-pointer")

    let changeColors = () => {
        if(btn =="flex justify-around mr-4 border-2 w-20 py-1 flex-row rounded-full"){
            setBtn("flex justify-around mr-4 border-2 w-20 py-1 flex-row rounded-full changebck")
            setColorP("text-white")
            setColorCart("h-6 w-6 text-white cursor-pointer")
        } else {
            setBtn("flex justify-around mr-4 border-2 w-20 py-1 flex-row rounded-full")
            setColorP("text-gray-500")
            setColorCart("h-6 w-6 title cursor-pointer")
        }
    }

    return (
        <div>
            <div className={btn} onMouseUp={changeColors} >
                    <a>
                        <svg className={colorCart}  fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z"/>
                        </svg>
                    </a>
                    <p className={colorP}> {initialValue} </p>
            </div>
        </div>
    )
}
