import './App.css';
import "@material-tailwind/react/tailwind.css";
import Header from './header/Header'
import Main from './main/Main'
import Footer from './footer/Footer'
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";
import { useState } from 'react';

export default function App() {

  const [notVisible] = useState("navBarLayout")

  const [position, setPosition] = useState("rotate-0")

  /* const [colorP, setColorP] = useState(props.color) 
   // VERIFICAR PORQUE O MENU PASSA PRIMEIRO PARA CINZENTO E DEPOIS RODA!!
   // porquê estar da dar só no segundo clique
   const [noView, setNoView] = useState(props.hideNav) */

  let changingMenu = () => {
        if(position == "rotate-0 duration-300"){
            setPosition("rotate-90 duration-300 mx-10")
            setColorP("h-8 w-16 title duration-300")
            setNoView(props.showNav)
        } else {
            setPosition("rotate-0 duration-300")
            setColorP(props.color)
            setNoView("navBarLayout")
        }
    }   




  return (
    <div className="App">
      <div>
        <nav className={notVisible}>
          <ul>
              <li className="items">
                Home 
              </li> 
              <li className="items1">
                Wish Catalog 
              </li>
          </ul> 
        </nav>
      </div>
        <Header />
        <Main />
    </div>
  );
}
