import { useEffect, useState } from 'react'
import './catalogPage.css'

export default function CatalogPage() {

    const [productsData, setProductsData] = useState([])

    const [afterBreakfastData, setAfterBreakfastData] = useState([])

    const [buttonRotation, setButtonRotation] = useState("plusButton")



    useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/wishCatalog")
        .then((resp) => resp.json())
        .then((data) => setBreakfastData(data))        
    }, []) // API breakfast


    let click = (evt) => { //USAR EVENT.TARGET 
        if(buttonRotation == "plusButton"){
            setButtonRotation("plusButton1")
        } else {
            setButtonRotation("plusButton")
        }
    }

    return (
            <div className="itemsContainer">
            <ul className="itemsCatalog">
                    {
                        breakfastData.map(products => (
                            <li className="isolatedItems" key={products.id}>
                                    <h2> 
                                    {products.name} 
                                    <svg class={buttonRotation} onClick={click} fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"/>
                                    </svg>
                                    </h2>
                                <img src={products.image} />
                            </li>
                        ))
                    }
            </ul>
        </div>
    )
}
