import React, { useState } from 'react'
import './catalogPage.css'

export default function CatalogPage(props) {

    const [currentPage, setCurrentPage] = useState(1)

    const productsPerPage = 6

    const maxPageLimit = 3

    const minPageLimit = 1

    const changePage = (event) => { // identifica a página em que o utilizador clickou
        setCurrentPage(event.target.id)
    } 

    const previousBtn = () => {
        if(currentPage - 1 <  minPageLimit){
            setCurrentPage(minPageLimit)
        } else {
            setCurrentPage(currentPage - 1)
        }
    }

    const nextBtn = () => {
        if(currentPage + 1 > maxPageLimit){
            setCurrentPage(minPageLimit)
        } else {
            setCurrentPage(currentPage + 1)
        }
    }

    const pages = []

    for(let i = 1; i <= Math.ceil(props.products.length / productsPerPage); ++i) {
        pages.push(i)
    }

    const indexlastProduct = currentPage * productsPerPage 

    const indexFirstProduct = indexlastProduct - productsPerPage

    const currentProducts = props.products.slice(indexFirstProduct, indexlastProduct)

    console.log(currentProducts)

    const renderProducts = product => { // renderiza todos os cards dos produtos na view
        return(
            <ul className="itemsCatalog">
                {
                    product.map((product, index) => {
                        return (
                            <li 
                            className="isolatedItems"
                            key={index}
                            onClick={props.openCard}
                            >
                                <h2>{product.name}</h2>
                                <img src={product.image} alt={product.name}/>
                            </li>
                        )
                    } )
                }  
            </ul>
        )
    }

    const renderPageNumbers = pages.map(number => { // renderiza o layout de mudança de página e suas animações
        if(number < maxPageLimit + 1 && number >= minPageLimit){
            return (
                <li
                className={currentPage == number ? "activePage": "disabePage"}
                key={number} 
                id={number}
                onClick={changePage}
                >
                .
                </li>
            )
        } else {
            return null
        }
    })

    return (
            <div className="itemsContainer">
                {renderProducts(prop.currentProducts)}
            <ul className="paginateContainer">
                <svg class="previousBtn" onClick={previousBtn}  viewBox="0 0 24 24"  fill="none"  stroke="currentColor"  stroke-width="2"  stroke-linecap="round"  stroke-linejoin="round">  
                    <circle cx="12" cy="12" r="10" />  
                    <polyline points="12 8 8 12 12 16" />  
                    <line x1="16" y1="12" x2="10.5" y2="12" />
                </svg>
                {renderPageNumbers}
                <svg class="nextBtn" onClick={nextBtn}  viewBox="0 0 24 24"  fill="none"  stroke="currentColor"  stroke-width="2"  stroke-linecap="round"  stroke-linejoin="round">  
                    <circle cx="12" cy="12" r="10" />  
                    <polyline points="12 16 16 12 12 8" />  
                    <line x1="8" y1="12" x2="13.5" y2="12" />
                </svg>
            </ul>
            </div>
    )
}
