import React, { useState } from 'react'
import './catalogPage.css'
import ReactPaginate from 'react-paginate'

export default function CatalogPage(props) {

    const [currentPage, setCurrentPage] = useState(1)

    const [productsPerPage, setProductsPerPage] = useState(6)

    const pages = []

    for(let i = 0; i < Math.ceil(props.products.length / productsPerPage); ++i) {
        pages.push(i)
    }

    const indexOflastProduct = currentPage * productesPerPage

    const renderProducts = product => {
        return(
            <ul className="itemsCatalog">
                {
                    product.map((product, index) => {
                        return (
                            <li 
                            className="isolatedItems"
                            key={index}
                            >
                                <h2>{product.name}</h2>
                                <img src={product.image} alt={product.name}/>
                            </li>
                        )
                    } )
                }  
            </ul>
        )
    }

    const renderPageNumbers = pages.map(number => {
        return (
                <li
                key={number} 
                id={number}
                >
                .
                </li>
        )
    })

    return (
            <div className="itemsContainer">
                {renderProducts(props.products)}
            <ul className="paginateContainer">
                {renderPageNumbers}
            </ul>
            </div>
    )
}
