import { useState , } from 'react'
import './inputFiltrarProdutos.css'

export default function InputFiltrarProdutos() {

    const [width, setWidth] = useState("w-20 border-2 rounded-full py-1")

    const [placeholder, setPlaceholder] = useState("")



    let increaseWidth = () => {
        /* if(width == "w-12 border-2 rounded-full py-1 px-1.5 duration-1000"){ */
            setWidth("w-96 border-2 rounded-full py-1 pl-9 duration-1000")
            setPlaceholder("Type your wishes...")
        /* } /* else { 
            setWidth("w-12 border-2 border-gray-600 rounded-full py-1 px-1.5 duration-1000")
            setPlaceholder("")
        } */
    }

   let decreasevalue = () => {
        setWidth("w-20 border-2 rounded-full py-1 px-1.5 duration-1000")
        setPlaceholder("")
    } 

    return (
        <div className="mx-10 relative"> 
            <div>
                <a className="cursor-pointer">
                    <svg className="h-6 w-6 text-yellow-800 absolute top-1.5 right-3"  width="24" height="19" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round">  <path stroke="none" d="M0 0h24v24H0z"/>  <circle cx="10" cy="10" r="7" />  <line x1="21" y1="21" x2="15" y2="15" /></svg>
                </a>
            </div>
            <div className="cursor">
                <a>
                    <svg class="h-6 w-6 text-gray-500 absolute top-1.5 left-3 "  width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round">  <path stroke="none" d="M0 0h24v24H0z"/>  <polyline points="15 6 9 12 15 18" /></svg>
                </a>
            </div>
            <form>
                <label>
                    <input className={width} onMouseEnter={increaseWidth} onDoubleClick={decreasevalue} type="text" placeholder={placeholder} onFocus="border-zinc-700" />
                </label>
            </form>
        </div>
    )
}
