import {useState} from "react";
import MenuButton from '../ReusableComponents/MenuButton';

export default function Header()  {

    let userName = prompt("Bem-vindo à nossa página! Com que nome é que te apresentas?")

    return (
        <div className=" m-0 w-screen h-4 items-center">
            <div className="mx-0.5 flex items-center">
                <a className=" mx-1 cursor-pointer">
                <MenuButton />
                </a>
                <h1 className="mx-7 flex flex-row items-center"> Bem vindo à <span className="mx-2 text-2xl">Food Wish</span>, {userName} ! </h1>
            </div>
        </div>
    )
}
