import React, { createContext, useEffect, useState } from 'react'
import './catalogPage.css'
import ReactPaginate from 'react-paginate'

export const ProductsContext = createContext();

export default function CatalogPage(props) {

    const [products, setProducts] = useState([])

    const [totalProducts] = [products.slice(0, 18)] 
    // Como existem 18 produtos, irão existir 3 páginas, pois cada uma contém 6 produtos

    const [pageNumber, setPageNumber] = useState(0)

    const productsPerPage = 6

    const pagesVisited = pageNumber * productsPerPage

    const pageCount = Math.ceil(totalProducts.length / productsPerPage)

    const changePage = ({selected}) => {
        setPageNumber(selected)
    }

    const displayProducts = totalProducts
    .slice(pagesVisited, pagesVisited + productsPerPage)
    .map(product =>{
        return (
            <li className="isolatedItems cursor-pointer" key={product.id} onClick={props.modalChange}> {/* Click para abrir modal */}
                <h2> 
                {product.name} 
                </h2>
                <img src={product.image} />
            </li>
        )
    }) 




    useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/wishCatalog")
        .then((resp) => resp.json())
        .then((data) => setProducts(data))        
    }, []) 


    return (
        <>
        <ProductsContext.Provider value={productData}>
            <div className="itemsContainer">
                <ul className="itemsCatalog">
                    {displayProducts}
                </ul>
                <ReactPaginate 
                    pageCount={pageCount}
                    onPageChange={changePage}
                    containerClassName={"paginateContainer"}
                    activeClassName={"paginationEnable"}
                />
            </div>
        </ProductsContext.Provider>
        </>
    )
}
