import { useState } from "react"
import MenuButton from '../ReusableComponents/MenuButton';
import './header.css'

export default function Header()  {

    const [userName, setUserName] = useState(prompt("Bem-vindo à nossa página! Com que nome é que te apresentas?"))

    return (
        <div className="m-0 w-screen h-20 flex items-center">
            <div className="mx-0.5 flex items-center">
                <a className=" mx-4 cursor-pointer">
                <MenuButton />
                </a>
                <h1 className="mx-7 flex flex-row items-center fontText"> Bem vindo à <span className="mx-2 text-2xl fontBrand">Food Wish</span>, {userName} ! </h1>
            </div>
        </div>
    )
}

