import React, { useEffect, useState } from 'react'
import './main.css'
import CatalogPage from '../ReusableComponents/CatalogComp/CatalogPage'
import Modal from '../ReusableComponents/modalComp/Modal'

export default function Main(props) {

     const [currentPage, setCurrentPage] = useState(0)

     const productsPerPage = 6

     /* const [indexOfProduct, setIndexOfProduct] = useState(0) 

     const productsInGroup = props.products.filter( (product, i) => i == indexOfProduct) */

    const indexOfLastItem = currentPage * productsPerPage

    const indexOfFirstItem = indexOfLastItem - productsPerPage

    const currentItems = products.slice(indexOfProduct, indexOfLastItem)

    const [modalFirstState, setModaFirstlState] = useState("modalContainer1") 
    //estado não visivel

    const [modalSecondState, setmodalSecondState] = useState("modalContainer")
    //estado visivel

     let openModal = (index) => {
        setIndexOfProduct(index)
        if(modalFirstState === "modalContainer1"){ 
           setModaFirstlState(modalSecondState)
           setmodalSecondState("modalContainer1")
        }
    }

    let closeModal = () => {
        if(modalFirstState === "modalContainer") {
            setModaFirstlState(modalSecondState)
            setmodalSecondState("modalContainer")
        }
    } 


    useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/wishCatalog")
        .then((resp) => resp.json())
        .then((data) => setProducts(data))        
    }, []) 


    return (
        <>
            <div className="catalogContainer relative" id="main">
                <CatalogPage
                    products={products}
                    pageNumber={currentPage}
                    productsPerPage={productsPerPage}
                    openModal={openModal}
                    indexOfProduct={indexOfProduct}
                    productsInGroup={productsInGroup}
                />
                { 
                    productsInGroup.map(product => 
                        <Modal 
                        name={product.name}
                        image={product.image}
                        meal={product.meal}
                        description={product.description}
                        extras={product.extras}
                        price={product.price}
                        stock={product.stock}
                        hide={modalFirstState} 
                        show={modalSecondState}
                        closeModal={closeModal}
                        currentItems={currentItems}
                        /> 
                    )
                }
            </div>
        </>
    )
}
