import React, { useState } from 'react'
import './catalogPage.css'
import ReactPaginate from 'react-paginate'

export default function CatalogPage(props) { 

    const [lengthOfProductsArr] = useState(props.product.length)

    const [totalProducts] = [props.products.slice(0, props.product.length)] 
    // Como existem 18 produtos, irão existir 3 páginas, pois cada uma contém 6 produtos
    const [pageNumber1, setPageNumber1] = useState(props.pageNumber)

    const productsPerPage1 = props.productsPerPage

    const pagesVisited = pageNumber1 * productsPerPage1

    const pageCount = Math.ceil(totalProducts.length / productsPerPage1)

    const changePage = ({selected}) => {
        setPageNumber1(selected)
    }

    const displayProducts = totalProducts
    .slice(pagesVisited, pagesVisited + productsPerPage1)
    .map(product =>{
        return (
            <li className="isolatedItems cursor-pointer" key={product.id} onClick={props.modalChange}> {/* Click para abrir modal */}
                <h2> 
                {product.name} 
                </h2>
                <img src={product.image} />
            </li>
        )
    }) 

    return (
            <div className="itemsContainer">
                <ul className="itemsCatalog">
                    {displayProducts}
                </ul>
                <ReactPaginate 
                    pageCount={pageCount}
                    onPageChange={changePage}
                    containerClassName={"paginateContainer"}
                    activeClassName={"paginationEnable"}
                />
            </div>
    )
}
