import { useEffect, useState } from 'react'
import './catalogPage.css'

export default function CatalogPage() {

    const [breakfastData, setBreakfastData] = useState([])

    const [afterBreakfastData, setAfterBreakfastData] = useState([])

    const [buttonRotation, setButtonRotation] = useState("plusButton")

    useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/Breakfast")
        .then((resp) => resp.json())
        .then((data) => setBreakfastData(data))        
    }, []) // API breakfast

    useEffect(() => { 
        fetch("https://61e59d49c14c7a0017124d7d.mockapi.io/api/AfterBreakfastMeals")
        .then((resp) => resp.json())
        .then((data) => setAfterBreakfastData(data))        
    }, []) // API AfterBreakfastMeals

    let click = (evt) => { //USAR EVENT.TARGET 
        if(buttonRotation == "plusButton"){
            setButtonRotation("plusButton1")
        } else {
            changeStyle.setButtonRotation("plusButton")
        }
    }


    return (
        <div className="catalogContainer">
            <div className="itemsContainer">
            <ul className="itemsCatalog">
                    {   
                        breakfastData.map(productsB => (
                            <li className="isolatedItems" key={productsB.id}>
                                    <h2> 
                                    {productsB.name} 
                                    <svg class={buttonRotation} onClick={click} fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"/>
                                    </svg>
                                    </h2>
                                <img src={productsB.image} />
                            </li>
                        ))
                    }
                </ul>
                <div className="flagContainer">
                <h2>Wish Catalog</h2>
                <div className="whiteBox"></div>
                <div className="whiteBox1"></div>
            </div>
            </div>
        </div>
    )
}
