import { useEffect, useState } from 'react';
import './modal.css'

export default function Modal(props) {

    const [stock, setStock] = useState(props.stock) 




    /* onClick={props.closeModal} */ // evento para fechar modal
  return (
    <div className={props.hide} >
        <div className="infoContainer">
          <svg class="h-10 w-10 text-white absolute z-10 top-4 right-3 cursor-pointer" onClick={props.closeModal} fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"/>
          </svg>
          <h1>{props.name}</h1>
          <h2 className="price"> €{props.price}</h2>
          <img className="imageModal" src={props.image} />
          <p className="description">{props.description}</p>
          <h2 className="extrasTitle">Extras</h2>
          {
            props.extras.map((extra, index) =>(
              <div className="checkBoxContainer">
                <input type="checkbox" id={index} name={extra} />
                <label className="ml-3" for={extra}>{extra}</label>
              </div>
            ))    
          }
          <div className="modalPriceStock">
            <p>
              Stock:
              {
                stock <= 10 && stock > 0 ? <span className="visaSign"> ✓ </span> : <span> x </span>
              }
            </p>
          </div>
        </div>
    </div>
    )
}
