import { useEffect, useState } from 'react';
import './modal.css'

export default function Modal(props) {

    const [modalHide, setModalHide] = useState(props.hide)

    const [modalShow] = useState(props.show)

    let closeModal = () => {
        if(modalHide == "modalContainer")
        setModalHide(props.hide)
    }

  return (
    <div className={props.hide} onChange={props.hide}>
        <button className="h-12 w-12 bg-white ml-4 mt-4" onClick={closeModal}>CLICK ME</button>
    </div>
    )
}
