import React, { useEffect, useState } from 'react'
import './main.css'
import CatalogPage from '../ReusableComponents/CatalogComp/CatalogPage'
import Modal from '../ReusableComponents/modalComp/Modal'

export default function Main(props) {

    const [modalFirstState, setModaFirstlState] = useState("modalContainer1") 

    const [modalSecondState] = useState("modalContainer")

   /*  const productsInGroup = props.products.filter( (product, i) => i == indexOfProduct) */

    let openModal = (evt) => {
       setModaFirstlState(evt.target["modalSecondState"])
    }

    let closeModal = () => {
        setModaFirstlState("modalContainer1")
    }

     return (
         <div className="catalogContainer relative" id="main">
            <CatalogPage
                products={props.products}
                openCard={openModal}
                closeModel={closeModal}
            />
             {
                props.products.map(product => 
                    <Modal 
                        id={product.id}
                        name={product.name}
                        image={product.image}
                        meal={product.meal}
                        description={product.description}
                        extras={product.extras}
                        price={product.price}
                        stock={product.stock}
                        hide={modalFirstState}
                    />
                ) 
            }
         </div>
     )
}
